/**
 * Build the site
 */
declare const build: (options?: {}, mode?: string) => void;

/**
 * Initialize a new Nanogen site from a predefined template.
 */
declare const init: (templateDir?: string) => Promise<void>;

/**
 * Parse options, setting the defaults on missing values
 */
interface BuildOptions {
    srcPath: string;
    outputPath: string;
    cleanUrls: boolean;
}
interface SiteOptions {
    [key: string]: any;
}
interface Options {
    build?: Partial<BuildOptions>;
    site?: SiteOptions;
}

interface ServeFlags {
    port?: string;
}
/**
 * Serve the site in watch mode
 */
declare const serve: (options: Options, flags: ServeFlags) => void;

export { build, init, serve };
